
jQuery.extend(true,{alertToaster:{oAlert:null,setAlert:function(p_oAlert){this.oAlert=p_oAlert;},_getAlert:function(){return this.oAlert;},createAlert:function(){var oAlertToaster=this,oAlert=(function(){var oAlert=this;this.baseZIndex=9999;this.oButtonEl=jQuery('<a href="#"><span>OK</span></a>').click(function(){oAlert.hide();return false;});this.oButtonContainerEl=jQuery('<p id="alerttoaster-button" />').append(this.oButtonEl);this.oMessageEl=jQuery('<p id="alerttoaster-message" />');this.oAlertEl=jQuery('<div id="alerttoaster-alert" />').css({position:'absolute',top:0,zIndex:oAlert.baseZIndex+1,display:'none'}).click(function(){oAlert.oButtonEl.focus();}).keydown(function(p_oEvent){if(p_oEvent.keyCode===27){oAlert.hide();}}).append(this.oMessageEl).append(this.oButtonContainerEl).appendTo('body');this.oBackgroundEl=jQuery('<div id="alerttoaster-background" />').css({position:'absolute',left:0,top:0,zIndex:oAlert.baseZIndex,display:'none',width:'100%',height:'100%'}).click(function(){oAlert.oButtonEl.focus();}).appendTo('body');this.getEl=function(){return this.oAlertEl;};this.setMessage=function(p_message){this.oMessageEl.html(p_message);};this.show=function(p_message){var oAlert=this;this.setMessage(p_message);this.oBackgroundEl.fadeIn('fast',function(){oAlert.getEl().slideDown('fast');oAlert.oButtonEl.focus();});};this.hide=function(){var oAlert=this;this.getEl().slideUp('fast',function(){oAlert.setMessage('');oAlert.oBackgroundEl.fadeOut('fast');});};return this;}());jQuery(window).resize(function(){oAlertToaster.positionAlert();});return oAlert;},getAlert:function(){if(this._getAlert()===null){this.setAlert(this.createAlert());}
return this._getAlert();},positionAlert:function(){var oAlertEl=this.getAlert().getEl();oAlertEl.css('left',((jQuery(window).width()/2)-(oAlertEl.width()/2))+'px');},showAlert:function(p_message){var oAlert=this.getAlert();this.positionAlert();oAlert.show(p_message);},hideAlert:function(){this.getAlert().hide();}}});